mapper.select:

select no, title, writer, 
to_char(reg_date, 'yyyy-mm-dd') as reg_date,   view_Cnt        
 from tbl_board  


   title like #{contents}


   writer like #{contents}


order by no desc

mapper.update:
update tbl_board 
set view_cnt = nvl(view_cnt,0) + 1 
where no = #{aa}

update tbl_board 
set title = #{title}, writer = #{writer}, content = #{content}
where no = #{no}
